import Axios from "axios";
/**
 * @description: 获取AccessCode-用于登录
 * @param {*} phone 账号-手机号
 * @param {*} password 密码
 * @return {*} code
 */
async function getAccessCode(phone, password) {
  phone = `+86${phone}`;
  let url = `https://api-user.huami.com/registrations/${phone}/tokens`;
  let data = new URLSearchParams({
    phone_number: phone,
    password,
    state: "REDIRECTION",
    client_id: "HuaMi",
    country_code: "CN",
    token: "access",
    redirect_uri: "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
  });
  let res = await Axios.post(url, data, {
    maxRedirects: 0,
    validateStatus: (status) => {
      return (status >= 200 && status < 300) || status == 303;
    },
  });
  const {
    headers: { location },
  } = res;
  let code = new URL(location).searchParams.get("access");
  return code;
}

/**
 * @description: 登录获取用户信息
 * @param {*} code AccessCode码
 * @return {*} {userid,apptoken}
 */
async function login(code) {
  let url = `https://account.huami.com/v2/client/login`;
  let data = new URLSearchParams({
    app_name: "com.xiaomi.hm.health",
    country_code: "CN",
    code,
    device_id: "be49ae737faf717b",
    device_model: "android_phone",
    app_version: "5.3.0",
    grant_type: "access_token",
    allow_registration: "false",
    dn: "account.huami.com,api-user.huami.com,api-watch.huami.com,auth.huami.com,api-analytics.huami.com,app-analytics.huami.com,api-mifit.huami.com",
    third_name: "huami_phone",
    source: "com.xiaomi.hm.health:5.3.0:50464",
    lang: "zh",
  });
  let res = await Axios.post(url, data);
  return {
    userid: res.data.token_info.user_id,
    apptoken: res.data.token_info.app_token,
  };
}

/**
 * @description: 提交同步
 * @param {*} userid 用户id
 * @param {*} apptoken apptoken
 * @param {*} step 步数
 * @return {*} result
 */
async function submit(userid, apptoken, step) {
  let data = `last_source=2&userid=${userid}&last_sync_data_time=1628831329&callid=1628831555709&channel=Normal&country=CN&cv=50464_5.3.0&data_json=%5B%7B%22date%22%3A%22${new Date()
    .toLocaleDateString()
    .split("/")
    .join(
      "-"
    )}%22%2C%22data%22%3A%5B%7B%22start%22%3A0%2C%22stop%22%3A1439%2C%22value%22%3A%%22%2C%22tz%22%3A32%2C%22did%22%3A%22-1%22%2C%22src%22%3A2%7D%5D%2C%22summary%22%3A%22%7B%5C%22v%5C%22%3A6%2C%5C%22slp%5C%22%3A%7B%5C%22st%5C%22%3A1628697600%2C%5C%22ed%5C%22%3A1628697600%2C%5C%22dp%5C%22%3A0%2C%5C%22lt%5C%22%3A0%2C%5C%22wk%5C%22%3A0%2C%5C%22usrSt%5C%22%3A-1440%2C%5C%22usrEd%5C%22%3A-1440%2C%5C%22wc%5C%22%3A0%2C%5C%22is%5C%22%3A0%2C%5C%22lb%5C%22%3A0%2C%5C%22to%5C%22%3A0%2C%5C%22dt%5C%22%3A0%2C%5C%22rhr%5C%22%3A0%2C%5C%22ss%5C%22%3A0%7D%2C%5C%22stp%5C%22%3A%7B%5C%22ttl%5C%22%3A${step}%2C%5C%22dis%5C%22%3A11%2C%5C%22cal%5C%22%3A1%2C%5C%22wk%5C%22%3A1%2C%5C%22rn%5C%22%3A0%2C%5C%22runDist%5C%22%3A0%2C%5C%22runCal%5C%22%3A0%7D%2C%5C%22goal%5C%22%3A7000%2C%5C%22tz%5C%22%3A%5C%2228800%5C%22%7D%22%2C%22source%22%3A2%2C%22type%22%3A2%7D%5D&device=android_30&device_type=2&lang=zh_CN&last_deviceid=-1&timezone=Asia%2FShanghai&uuid=null&v=2.0`;
  let res = await Axios.post("https://api-mifit.huami.com/v1/data/band_data.json", data, {
    headers: {
      apptoken,
    },
  });
  return res.data;
}

/**
 * @description: 开始提交
 * @param {Number} phone 账号
 * @param {*} password 密码
 * @param {*} step 步数
 * @return {*} result
 */
async function start(phone, password, step) {
  let result = null;
  try {
    let code = await getAccessCode(phone, password);
    const { userid, apptoken } = await login(code);
    result = await submit(userid, apptoken, step);
    return result;
  } catch (error) {
    return {
      code: 2,
      message: "error",
    };
  }
}

